// Running-Flag aus Context oder true initial
let running = context.get('running');
if (running === undefined) {
    running = true;
    context.set('running', true);
}

// Steuerung: topic == 'control' → running ein-/ausschalten
if (msg.topic === 'control') {
    context.set('running', msg.payload === true);
    return null;
}

// Wenn gestoppt, nichts ausgeben
if (!running) {
    return null;
}

// Nur echte Zahlen durchlassen
if (typeof msg.payload !== 'number' || isNaN(msg.payload)) {
    return null;
}

// Wertebereich prüfen und auf drei Ausgänge verteilen
let v = msg.payload;
let outputs = [null, null, null];
let sensor_stop = global.get("sensor_stop");
if (sensor_stop == false){
    if (v < -125) {
        outputs[0] = msg;
    } else if (v > 125) {
        outputs[1] = msg;
    } else {
        outputs[2] = msg;
    }
}
return outputs;

if (global.get("range_front_left")  < 0.2 || 
    global.get("range_front_right") < 0.2 ||
    global.get("area") > global.get("area_user_set") ){
    global.set("sensor_stop", true);
}
else{
    global.set("sensor_stop", false);
}
msg.payload = global.get("sensor_stop");
return msg;
